import React, { useState } from "react";

function Rastreio() {
  const [docNumber, setDocNumber] = useState("");
  const [docPrefix, setDocPrefix] = useState("957");
  const [soType, setSoType] = useState("SO");
  const [rastreamentoHtml, setRastreamentoHtml] = useState("");

  const gerarLinkRastreio = async () => {
    if (!docNumber) {
      alert("Por favor, insira o número do AWB.");
      return;
    }

    try {
      const response = await fetch(
        `http://localhost:8000/api/rastreio/?docNumber=${docNumber}&docPrefix=${docPrefix}&soType=${soType}`
      );
      const data = await response.json();

      if (data.error) {
        setRastreamentoHtml(`<p>${data.error}</p>`);
      } else {
        setRastreamentoHtml(data.html);
      }
    } catch (error) {
      setRastreamentoHtml("<p>Erro ao buscar rastreamento.</p>");
    }
  };

  const abrirNovaAba = () => {
    window.open(
      `https://www.latamcargo.com/pt/trackshipment?docNumber=${docNumber}&docPrefix=${docPrefix}&soType=${soType}`,
      "_blank"
    );
  };

  return (
    <div>
      <h2>Rastreio de Encomenda</h2>
      <label>Tipo:</label>
      <select value={soType} onChange={(e) => setSoType(e.target.value)}>
        <option value="SO">Doméstico</option>
        <option value="MAWB">Internacional</option>
      </select>
      <label>Prefixo:</label>
      <input
        type="text"
        value={docPrefix}
        onChange={(e) => setDocPrefix(e.target.value)}
      />
      <label>Número do AWB:</label>
      <input
        type="text"
        value={docNumber}
        onChange={(e) => setDocNumber(e.target.value)}
      />

      <button onClick={gerarLinkRastreio}>Rastrear</button>
      <button onClick={abrirNovaAba}>Abrir em Outra Aba</button>

      <div>
        <h3>Resultado do Rastreamento:</h3>
        <div dangerouslySetInnerHTML={{ __html: rastreamentoHtml }} />
      </div>

      {/* Adicionando o resultado da cotação */}
      <div className="resultado-container">
        <h2>Resultado da Cotação</h2>
        {rastreamentoHtml ? (
          <div dangerouslySetInnerHTML={{ __html: rastreamentoHtml }} />
        ) : (
          <p>Aqui será exibido o resultado do cálculo.</p>
        )}
      </div>
    </div>
  );
}

export default Rastreio;
