import React, { useState } from "react";
import "../assets/css/Cotacao.css";

const Cotacao = () => {
  const [form, setForm] = useState({
    cepOrigem: "74672856",
    cepDestino: "",
    destino: "",
    valorNotaFiscal: "R$ 0,00",
    entregaDomicilio: false,
    retiraTerminal: false,
    pagamentoOrigem: false,
    pagamentoDestino: false,
    adValorem: false,
    semSeguro: false,
    volumes: [{ altura: 10, largura: 10, comprimento: 10, peso: 0 }],
  });

  // Formata o CEP corretamente
  const formatCEP = (cep) => {
    let formattedCEP = cep.replace(/\D/g, "").slice(0, 8);
    return formattedCEP.length > 5
      ? formattedCEP.replace(/^(\d{5})(\d{3})$/, "$1-$2")
      : formattedCEP;
  };

  // Formata valores monetários
  const formatCurrency = (value) => {
    let numericValue = value.replace(/\D/g, "");
    let floatValue = numericValue ? parseFloat(numericValue) / 100 : 0;
    return numericValue
      ? floatValue.toLocaleString("pt-BR", {
          style: "currency",
          currency: "BRL",
          minimumFractionDigits: 2,
        })
      : "";
  };

  // Busca a cidade pelo CEP e atualiza o campo de destino
  const buscarCep = async (cep) => {
    let formattedCep = cep.replace(/\D/g, "").slice(0, 8);
    if (formattedCep.length !== 8) return;

    try {
      const response = await fetch(
        `https://viacep.com.br/ws/${formattedCep}/json/`
      );
      const data = await response.json();

      if (data.erro) {
        alert("CEP inválido! Verifique e tente novamente.");
        return;
      }

      setForm((prevForm) => ({
        ...prevForm,
        cepDestino: formattedCep,
        destino: `${data.localidade}-${data.uf}`, // Cidade e Estado
      }));
    } catch (error) {
      console.error("Erro ao buscar o CEP:", error);
      alert("Erro ao buscar o CEP. Tente novamente.");
    }
  };

  // Calcula o seguro Ad-Valorem (0,7% do valor da Nota Fiscal)
  const calcularSeguro = () => {
    let valorProdutosNumerico =
      parseFloat(
        form.valorNotaFiscal.replace(/[^\d,]/g, "").replace(",", ".")
      ) || 0;
    return form.adValorem ? valorProdutosNumerico * 0.007 : 0;
  };

  // Envio da cotação para a API
  const calcularCotacao = async () => {
    const volumesFormatados = form.volumes.map((v) => ({
      altura: v.altura,
      largura: v.largura,
      comprimento: v.comprimento,
      peso: parseFloat(v.peso.toString().replace(",", ".")) || 0,
    }));

    const dados = {
      origem: "GYN",
      destino: form.destino,
      cepDestino: form.cepDestino,
      valorNotaFiscal:
        parseFloat(
          form.valorNotaFiscal.replace(/[^\d,]/g, "").replace(",", ".")
        ) || 0,
      seguro: calcularSeguro(),
      entregaDomicilio: form.entregaDomicilio,
      retiraTerminal: form.retiraTerminal,
      pagamentoOrigem: form.pagamentoOrigem,
      pagamentoDestino: form.pagamentoDestino,
      volumes: volumesFormatados,
    };

    try {
      const response = await fetch(
        "http://127.0.0.1:8000/api/calcular-cotacao/",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(dados),
        }
      );

      if (!response.ok) {
        throw new Error("Erro ao calcular cotação");
      }

      const resultado = await response.json();
      console.log("Resultado da Cotação:", resultado);
    } catch (error) {
      console.error("Erro na requisição:", error);
      alert("Erro ao calcular a cotação.");
    }
  };

  return (
    <div className="cotacao-container">
      <h1>Calculadora de Frete</h1>

      <div className="form-container">
        <div className="coluna-esquerda">
          <label>Origem:</label>
          <input type="text" value="GOIANIA-GO" disabled />

          <label>CEP de Destino:</label>
          <input
            type="text"
            name="cepDestino"
            value={form.cepDestino}
            onChange={(e) =>
              setForm({ ...form, cepDestino: formatCEP(e.target.value) })
            }
            onBlur={() => buscarCep(form.cepDestino)}
            placeholder="Digite o CEP"
          />

          <label>Destino:</label>
          <input type="text" value={form.destino} disabled />

          <label>Valor total dos Produtos:</label>
          <input
            type="text"
            name="valorNotaFiscal"
            value={form.valorNotaFiscal}
            onChange={(e) =>
              setForm({
                ...form,
                valorNotaFiscal: formatCurrency(e.target.value),
              })
            }
          />

          <div className="checkbox-group seguro">
            <strong>Tipo de Seguro</strong>
            <button
              className={`option-button ${form.adValorem ? "active" : ""}`}
              onClick={() =>
                setForm({
                  ...form,
                  adValorem: !form.adValorem,
                  semSeguro: false,
                })
              }
            >
              Ad-Valorem
            </button>
            <button
              className={`option-button ${form.semSeguro ? "active" : ""}`}
              onClick={() =>
                setForm({
                  ...form,
                  semSeguro: !form.semSeguro,
                  adValorem: false,
                })
              }
            >
              Sem Seguro
            </button>
          </div>
        </div>

        <div className="coluna-direita">
          <div className="checkbox-group">
            <strong>Entrega</strong>
            <button
              className={`option-button ${
                form.entregaDomicilio ? "active" : ""
              }`}
              onClick={() =>
                setForm({
                  ...form,
                  entregaDomicilio: !form.entregaDomicilio,
                  retiraTerminal: false,
                })
              }
            >
              Entrega a Domicílio
            </button>
            <button
              className={`option-button ${form.retiraTerminal ? "active" : ""}`}
              onClick={() =>
                setForm({
                  ...form,
                  retiraTerminal: !form.retiraTerminal,
                  entregaDomicilio: false,
                })
              }
            >
              Retira no Terminal de Cargas
            </button>
          </div>

          <div className="checkbox-group">
            <strong>Pagamento</strong>
            <button
              className={`payment-button ${
                form.pagamentoOrigem ? "active" : ""
              }`}
              onClick={() =>
                setForm({
                  ...form,
                  pagamentoOrigem: true,
                  pagamentoDestino: false,
                })
              }
            >
              Origem
            </button>
            <button
              className={`payment-button ${
                form.pagamentoDestino ? "active" : ""
              }`}
              onClick={() =>
                setForm({
                  ...form,
                  pagamentoDestino: true,
                  pagamentoOrigem: false,
                })
              }
            >
              Destinatário
            </button>
          </div>
        </div>
      </div>

      <div className="button-group">
        <button className="calcular-button" onClick={calcularCotacao}>
          CALCULAR
        </button>
      </div>
    </div>
  );
};

export default Cotacao;
