import React, { useState } from "react";
import "../assets/css/Rastreio.css";

function Rastreio() {
  const [docNumber, setDocNumber] = useState("");
  const [docPrefix, setDocPrefix] = useState("957");
  const [soType, setSoType] = useState("SO");
  const [rastreamentoUrl, setRastreamentoUrl] = useState("");

  const gerarLinkRastreio = () => {
    if (!docNumber) {
      alert("Por favor, insira o número do AWB.");
      return;
    }

    const url = `https://www.latamcargo.com/pt/trackshipment?docNumber=${docNumber}&docPrefix=${docPrefix}&soType=${soType}`;
    setRastreamentoUrl(url);
  };

  return (
    <div className="rastreio-container">
      <h2>Rastreio de Encomenda</h2>

      {/* Formulário de Rastreamento */}
      <div className="rastreio-form">
        <label>Tipo:</label>
        <select value={soType} onChange={(e) => setSoType(e.target.value)}>
          <option value="SO">Doméstico</option>
          <option value="MAWB">Internacional</option>
        </select>

        <label>Prefixo:</label>
        <input
          type="text"
          name="prefixo"
          value={docPrefix}
          maxLength="3"
          onChange={(e) => setDocPrefix(e.target.value)}
        />

        <label>Número do AWB:</label>
        <input
          type="text"
          name="awb"
          value={docNumber}
          onChange={(e) => setDocNumber(e.target.value)}
          placeholder="Digite o AWB"
        />
      </div>

      {/* Botões */}
      <div className="rastreio-buttons">
        <button onClick={gerarLinkRastreio}>Rastrear</button>
      </div>

      {/* Exibir o iframe se uma URL for gerada */}
      {rastreamentoUrl && (
        <div className="iframe-container">
          <h3>Resultado do Rastreamento</h3>
          <iframe
            src={rastreamentoUrl}
            width="100%"
            height="600px"
            style={{ border: "none" }}
            title="Rastreamento LATAM Cargo"
          />
        </div>
      )}
    </div>
  );
}

export default Rastreio;
