import React, { useState } from "react";
import styled from "styled-components";

// Styled Components
const Container = styled.div`
  max-width: 1000px;
  margin: auto;
  font-family: Arial, sans-serif;
`;

const FormContainer = styled.div`
  display: flex;
  justify-content: space-between;
  background: #f4f4f4;
  padding: 15px;
  border-radius: 15px;
`;

const ColunaEsquerda = styled.div`
  display: flex;
  flex-direction: column;
  width: 50%;
`;

const ColunaDireita = styled.div`
  width: 50%;
`;

const Label = styled.label`
  margin-top: 10px;
  font-weight: bold;
`;

const Input = styled.input`
  width: 100%;
  padding: 8px;
  margin-top: 5px;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const CheckboxGroup = styled.div`
  background: #f9f9f9;
  padding: 15px;
  border-radius: 10px;
  margin-top: 10px;
  border: 1px solid #ddd;
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const CheckboxLabel = styled.label`
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 14px;
  color: #555;
  cursor: pointer;
`;

const Checkbox = styled.input`
  transform: scale(1.2);
  cursor: pointer;
`;

const ButtonGroup = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 20px;
`;

const Button = styled.button`
  padding: 10px;
  border: none;
  cursor: pointer;
  color: white;
  border-radius: 5px;
  font-size: 16px;
`;

const LimparButton = styled(Button)`
  background: red;
`;

const CalcularButton = styled(Button)`
  background: blue;
`;

const ResultadoContainer = styled.div`
  background: #e0e0e0;
  padding: 15px;
  margin-top: 20px;
  border-radius: 15px;
`;

const Cotacao = () => {
  const [form, setForm] = useState({
    cepOrigem: "74672856",
    cepDestino: "",
    valorNotaFiscal: "",
    entregaDomicilio: false,
    retiraTerminal: false,
    pagamentoOrigem: false,
    pagamentoDestino: false,
    volumes: [{ altura: 10, largura: 10, comprimento: 10, peso: 0 }],
  });

  // Manipular mudança de estado nos campos do formulário
  const handleChange = (e) => {
    const { name, type, checked } = e.target;
    setForm((prevForm) => ({
      ...prevForm,
      [name]: type === "checkbox" ? checked : e.target.value,
    }));
  };

  // Atualizar Entrega a Domicílio e Retirada no Terminal
  const handleEntregaChange = (tipo) => {
    if (tipo === "domicilio") {
      setForm((prevForm) => ({
        ...prevForm,
        entregaDomicilio: !prevForm.entregaDomicilio,
        retiraTerminal: prevForm.entregaDomicilio
          ? prevForm.retiraTerminal
          : false,
        pagamentoOrigem: true,
        pagamentoDestino: false,
      }));
    } else {
      setForm((prevForm) => ({
        ...prevForm,
        retiraTerminal: !prevForm.retiraTerminal,
        entregaDomicilio: prevForm.retiraTerminal
          ? prevForm.entregaDomicilio
          : false,
      }));
    }
  };

  // Regras de validação para Pagamento
  const validarPagamento = (tipo) => {
    if (tipo === "origem") {
      setForm((prevForm) => ({
        ...prevForm,
        pagamentoOrigem: true,
        pagamentoDestino: false,
      }));
    } else {
      if (form.entregaDomicilio) {
        alert(
          "Pagamento no Destinatário não é permitido para Entrega a Domicílio."
        );
        return;
      }
      setForm((prevForm) => ({
        ...prevForm,
        pagamentoOrigem: false,
        pagamentoDestino: true,
      }));
    }
  };

  // Resetar o formulário
  const limparFormulario = () => {
    setForm({
      cepOrigem: "74672856",
      cepDestino: "",
      valorNotaFiscal: "",
      entregaDomicilio: false,
      retiraTerminal: false,
      pagamentoOrigem: false,
      pagamentoDestino: false,
      volumes: [{ altura: 10, largura: 10, comprimento: 10, peso: 0 }],
    });
  };

  // Simulação de cotação
  const calcularCotacao = () => {
    console.log("Cotação enviada:", form);
  };

  return (
    <Container>
      <h1>Calculadora de Frete</h1>

      <FormContainer>
        <ColunaEsquerda>
          <Label>Origem:</Label>
          <Input type="text" value="GOIANIA-GO" disabled />

          <Label>CEP de Destino:</Label>
          <Input
            type="text"
            name="cepDestino"
            value={form.cepDestino}
            onChange={handleChange}
          />

          <Label>Valor total dos Produtos:</Label>
          <Input
            type="text"
            name="valorNotaFiscal"
            value={form.valorNotaFiscal}
            onChange={handleChange}
          />
        </ColunaEsquerda>

        <ColunaDireita>
          <CheckboxGroup>
            <strong>Entrega a Domicílio</strong>
            <CheckboxLabel>
              <Checkbox
                type="checkbox"
                name="entregaDomicilio"
                checked={form.entregaDomicilio}
                onChange={() => handleEntregaChange("domicilio")}
              />
              Sim
            </CheckboxLabel>
          </CheckboxGroup>

          <CheckboxGroup>
            <strong>Retira no Terminal de Cargas</strong>
            <CheckboxLabel>
              <Checkbox
                type="checkbox"
                name="retiraTerminal"
                checked={form.retiraTerminal}
                onChange={() => handleEntregaChange("terminal")}
              />
              Sim
            </CheckboxLabel>
          </CheckboxGroup>

          <CheckboxGroup>
            <strong>Pagamento</strong>
            <CheckboxLabel>
              <Checkbox
                type="checkbox"
                name="pagamentoOrigem"
                checked={form.pagamentoOrigem}
                onChange={() => validarPagamento("origem")}
              />
              Origem
            </CheckboxLabel>
            <CheckboxLabel>
              <Checkbox
                type="checkbox"
                name="pagamentoDestino"
                checked={form.pagamentoDestino}
                onChange={() => validarPagamento("destino")}
              />
              Destinatário
            </CheckboxLabel>
          </CheckboxGroup>
        </ColunaDireita>
      </FormContainer>

      <ButtonGroup>
        <LimparButton onClick={limparFormulario}>LIMPAR</LimparButton>
        <CalcularButton onClick={calcularCotacao}>CALCULAR</CalcularButton>
      </ButtonGroup>

      <ResultadoContainer>
        <h2>Resultado da Cotação</h2>
        {form.retiraTerminal && form.cepDestino ? (
          <p>
            O cliente deverá retirar a mercadoria no aeroporto correspondente ao
            destino informado.
          </p>
        ) : (
          <p>Aqui será exibido o resultado do cálculo.</p>
        )}
      </ResultadoContainer>
    </Container>
  );
};

export default Cotacao;
