import React, { useState } from "react";
import "../assets/css/Cotacao.css";

const Cotacao = () => {
  const [form, setForm] = useState({
    cepOrigem: "74672856",
    cepDestino: "",
    valorNotaFiscal: "R$ 0,00",
    entregaDomicilio: false,
    retiraTerminal: false,
    pagamentoOrigem: false,
    pagamentoDestino: false,
    adValorem: false,
    semSeguro: false,
    volumes: [{ altura: 10, largura: 10, comprimento: 10, peso: "0,0" }],
  });

  // Função para formatar peso no formato "0,5"
  const formatPeso = (value) => {
    let pesoFormatado = value.replace(/[^0-9,]/g, "").replace(/^,/, "0,");

    // Impede múltiplas vírgulas
    if ((pesoFormatado.match(/,/g) || []).length > 1) {
      pesoFormatado = pesoFormatado.replace(/,(?=.*,)/g, "");
    }

    return pesoFormatado;
  };

  // Formatação ao sair do campo (onBlur)
  const formatPesoFinal = (index) => {
    let pesoAtual = form.volumes[index].peso;

    if (!pesoAtual || pesoAtual === ",") {
      pesoAtual = "0,0";
    }

    // Se não houver vírgula, adiciona ",0"
    if (!pesoAtual.includes(",")) {
      pesoAtual = `${pesoAtual},0`;
    }

    // Divide entre kg e gramas
    let [kg, g] = pesoAtual.split(",");

    // Remove zeros à esquerda do kg (mas mantém pelo menos "0")
    kg = kg.replace(/^0+/, "") || "0";

    // Garante que os gramas tenham 1 dígito
    g = (g || "").slice(0, 1).padEnd(1, "0");

    // Atualiza o valor formatado
    const newVolumes = [...form.volumes];
    newVolumes[index].peso = `${kg},${g}`;
    setForm({ ...form, volumes: newVolumes });
  };

  // Manipular mudanças nos volumes
  const handleVolumeChange = (index, e) => {
    const { name, value } = e.target;

    if (value === "") {
      const newVolumes = [...form.volumes];
      newVolumes[index][name] = "";
      setForm({ ...form, volumes: newVolumes });
      return;
    }

    if (name === "peso") {
      const newVolumes = [...form.volumes];
      newVolumes[index][name] = formatPeso(value);
      setForm({ ...form, volumes: newVolumes });
      return;
    }

    const parsedValue = parseInt(value.replace(/\D/g, ""), 10);
    if (!isNaN(parsedValue)) {
      const newVolumes = [...form.volumes];
      newVolumes[index][name] = parsedValue;
      setForm({ ...form, volumes: newVolumes });
    }
  };

  return (
    <div className="cotacao-container">
      <h1>Calculadora de Frete</h1>

      <div className="form-container">
        <div className="coluna-esquerda">
          <label>Origem:</label>
          <input type="text" value="GOIANIA-GO" disabled />

          <label>CEP de Destino:</label>
          <input
            type="text"
            name="cepDestino"
            value={form.cepDestino}
            onChange={handleVolumeChange}
            placeholder="Digite o CEP"
          />

          <label>Valor total dos Produtos:</label>
          <input
            type="text"
            name="valorNotaFiscal"
            value={form.valorNotaFiscal}
            onChange={handleVolumeChange}
          />

          <div className="checkbox-group seguro">
            <strong>Tipo de Seguro</strong>
            <div className="seguro-options">
              <button
                className={`option-button ${form.adValorem ? "active" : ""}`}
                onClick={() =>
                  setForm((prevForm) => ({
                    ...prevForm,
                    adValorem: !prevForm.adValorem,
                    semSeguro: false,
                  }))
                }
              >
                Ad-Valorem
              </button>
              <button
                className={`option-button ${form.semSeguro ? "active" : ""}`}
                onClick={() =>
                  setForm((prevForm) => ({
                    ...prevForm,
                    semSeguro: !prevForm.semSeguro,
                    adValorem: false,
                  }))
                }
              >
                Sem Seguro
              </button>
            </div>
          </div>
        </div>

        <div className="coluna-direita">
          <div className="checkbox-group">
            <strong>Entrega</strong>
            <button
              className={`option-button ${
                form.entregaDomicilio ? "active" : ""
              }`}
              onClick={() =>
                setForm((prevForm) => ({
                  ...prevForm,
                  entregaDomicilio: !prevForm.entregaDomicilio,
                  retiraTerminal: false,
                }))
              }
            >
              Entrega a Domicílio
            </button>
            <button
              className={`option-button ${form.retiraTerminal ? "active" : ""}`}
              onClick={() =>
                setForm((prevForm) => ({
                  ...prevForm,
                  retiraTerminal: !prevForm.retiraTerminal,
                  entregaDomicilio: false,
                }))
              }
            >
              Retira no Terminal de Cargas
            </button>
          </div>

          <div className="checkbox-group">
            <strong>Pagamento</strong>
            <button
              className={`payment-button ${
                form.pagamentoOrigem ? "active" : ""
              }`}
              onClick={() =>
                setForm((prevForm) => ({
                  ...prevForm,
                  pagamentoOrigem: true,
                  pagamentoDestino: false,
                }))
              }
            >
              Origem
            </button>
            <button
              className={`payment-button ${
                form.pagamentoDestino ? "active" : ""
              }`}
              onClick={() =>
                setForm((prevForm) => ({
                  ...prevForm,
                  pagamentoOrigem: false,
                  pagamentoDestino: true,
                }))
              }
            >
              Destinatário
            </button>
          </div>
        </div>
      </div>

      <div className="volumes-container">
        <h3>Dados do Volume</h3>
        {form.volumes.map((volume, index) => (
          <div key={index} className="volume-box">
            <label>Altura (cm)</label>
            <input
              type="number"
              name="altura"
              value={volume.altura}
              onChange={(e) => handleVolumeChange(index, e)}
            />

            <label>Largura (cm)</label>
            <input
              type="number"
              name="largura"
              value={volume.largura}
              onChange={(e) => handleVolumeChange(index, e)}
            />

            <label>Comprimento (cm)</label>
            <input
              type="number"
              name="comprimento"
              value={volume.comprimento}
              onChange={(e) => handleVolumeChange(index, e)}
            />

            <label>Peso (Kg)</label>
            <input
              type="text"
              name="peso"
              value={volume.peso}
              onChange={(e) => handleVolumeChange(index, e)}
              onBlur={() => formatPesoFinal(index)} // Formata ao sair do campo
            />
          </div>
        ))}
      </div>

      <button className="limpar-button">LIMPAR</button>
      <button className="calcular-button">CALCULAR</button>
    </div>
  );
};

export default Cotacao;
