import React, { useState } from "react";
import "../assets/css/Rastreio.css";

function Rastreio() {
  const [docNumber, setDocNumber] = useState("");
  const [rastreamentoDados, setRastreamentoDados] = useState(null);
  const [carregando, setCarregando] = useState(false);
  const [erro, setErro] = useState("");

  // ðŸ”¹ FunÃ§Ã£o para buscar os dados do Melhor Rastreio
  const gerarLinkRastreio = async () => {
    if (!docNumber) {
      alert("Por favor, insira o nÃºmero do rastreamento.");
      return;
    }

    setCarregando(true);
    setErro("");
    setRastreamentoDados(null);

    try {
      const response = await fetch(
        `http://localhost:8000/api/rastreio/?docNumber=${docNumber}`
      );
      const data = await response.json();

      if (data.error) {
        setErro(data.error);
      } else {
        setRastreamentoDados(data);
      }
    } catch (error) {
      setErro("Erro ao buscar rastreamento. Verifique sua conexÃ£o.");
    } finally {
      setCarregando(false);
    }
  };

  // ðŸ”¹ FunÃ§Ã£o para abrir a LATAM Cargo em outra aba
  const abrirNovaAba = () => {
    window.open(
      `https://www.latamcargo.com/pt/trackshipment?docNumber=${docNumber}&docPrefix=957&soType=SO`,
      "_blank"
    );
  };

  return (
    <div className="rastreio-container">
      <h2>Rastreio de Encomenda</h2>

      {/* ðŸ”¹ FormulÃ¡rio de Rastreamento */}
      <div className="rastreio-form">
        <label>NÃºmero de Rastreamento:</label>
        <input
          type="text"
          name="awb"
          value={docNumber}
          onChange={(e) => setDocNumber(e.target.value)}
          placeholder="Digite o cÃ³digo de rastreamento"
        />
      </div>

      {/* ðŸ”¹ BotÃµes */}
      <div className="rastreio-buttons">
        <button onClick={gerarLinkRastreio}>Rastrear</button>
        <button onClick={abrirNovaAba}>Abrir LATAM Cargo</button>
      </div>

      {/* ðŸ”¹ Carregamento */}
      {carregando && <p>Buscando informaÃ§Ãµes...</p>}

      {/* ðŸ”¹ Erro */}
      {erro && <p className="erro">{erro}</p>}

      {/* ðŸ”¹ Resultado do Rastreamento */}
      {rastreamentoDados && (
        <div className="resultado-container">
          <h2>Resultado do Rastreamento</h2>
          <p>
            <strong>Status:</strong> {rastreamentoDados.status}
          </p>

          <h3>Eventos</h3>
          <ul>
            {rastreamentoDados.eventos.map((evento, index) => (
              <li key={index}>
                {evento.data}: {evento.descricao}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Rastreio;
