import React, { useState } from "react";

function Rastreio() {
  const [docNumber, setDocNumber] = useState("");
  const [docPrefix, setDocPrefix] = useState("957"); // Prefixo padrão
  const [soType, setSoType] = useState("SO"); // Tipo Doméstico
  const [trackingResult, setTrackingResult] = useState(""); // Estado para armazenar o resultado do rastreio

  const gerarLinkRastreio = async () => {
    if (!docNumber) {
      alert("Por favor, insira o número do AWB.");
      return;
    }
    const url = `https://www.latamcargo.com/pt/trackshipment?docNumber=${docNumber}&docPrefix=${docPrefix}&soType=${soType}`;

    try {
      const response = await fetch(url);
      const html = await response.text();

      // Criar um DOM temporário para manipular o HTML retornado
      const parser = new DOMParser();
      const doc = parser.parseFromString(html, "text/html");

      // Seleciona o conteúdo do rastreio dentro da tag <u>
      const trackingElement = doc.querySelector("u");
      if (trackingElement) {
        setTrackingResult(trackingElement.innerText);
      } else {
        setTrackingResult("Nenhum resultado encontrado.");
      }
    } catch (error) {
      console.error("Erro ao buscar rastreio:", error);
      setTrackingResult("Erro ao buscar rastreio.");
    }
  };

  const abrirRastreioEmNovaAba = () => {
    if (!docNumber) {
      alert("Por favor, insira o número do AWB.");
      return;
    }
    const url = `https://www.latamcargo.com/pt/trackshipment?docNumber=${docNumber}&docPrefix=${docPrefix}&soType=${soType}`;
    window.open(url, "_blank"); // Abre em nova aba
  };

  return (
    <div>
      <h2>Rastreio de Encomenda</h2>
      <label>Tipo:</label>
      <select value={soType} onChange={(e) => setSoType(e.target.value)}>
        <option value="SO">Doméstico</option>
        <option value="MAWB">Internacional</option>
      </select>

      <label>Prefixo:</label>
      <input
        type="text"
        value={docPrefix}
        onChange={(e) => setDocPrefix(e.target.value)}
      />

      <label>Número do AWB:</label>
      <input
        type="text"
        value={docNumber}
        onChange={(e) => setDocNumber(e.target.value)}
      />

      <button onClick={gerarLinkRastreio}>Rastrear</button>
      <button onClick={abrirRastreioEmNovaAba}>Abrir em Outra Aba</button>

      {trackingResult && (
        <div>
          <h3>Resultado do Rastreamento:</h3>
          <p>{trackingResult}</p>
        </div>
      )}
    </div>
  );
}

export default Rastreio;
