import React, { useState } from "react";
import "../assets/css/Cotacao.css";

const Cotacao = () => {
  // Estado inicial do formulário
  const [form, setForm] = useState({
    cepOrigem: "74672856",
    cepDestino: "",
    valorNotaFiscal: "",
    entregaDomicilio: false,
    retiraTerminal: false,
    pagamentoOrigem: false,
    pagamentoDestino: false,
    volumes: [{ altura: 10, largura: 10, comprimento: 10, peso: 0 }],
  });

  // Manipular mudança de estado nos campos do formulário
  const handleChange = (e) => {
    const { name, type, checked } = e.target;
    setForm((prevForm) => ({
      ...prevForm,
      [name]: type === "checkbox" ? checked : e.target.value,
    }));
  };

  // Atualizar Entrega a Domicílio e Retirada no Terminal
  const handleEntregaChange = (tipo) => {
    if (tipo === "domicilio") {
      setForm((prevForm) => ({
        ...prevForm,
        entregaDomicilio: !prevForm.entregaDomicilio,
        retiraTerminal: prevForm.entregaDomicilio
          ? prevForm.retiraTerminal
          : false,
        pagamentoOrigem: true,
        pagamentoDestino: false,
      }));
    } else {
      setForm((prevForm) => ({
        ...prevForm,
        retiraTerminal: !prevForm.retiraTerminal,
        entregaDomicilio: prevForm.retiraTerminal
          ? prevForm.entregaDomicilio
          : false,
      }));
    }
  };

  // Regras de validação para Pagamento
  const validarPagamento = (tipo) => {
    if (tipo === "origem") {
      setForm((prevForm) => ({
        ...prevForm,
        pagamentoOrigem: true,
        pagamentoDestino: false,
      }));
    } else {
      if (form.entregaDomicilio) {
        alert(
          "Pagamento no Destinatário não é permitido para Entrega a Domicílio."
        );
        return;
      }
      setForm((prevForm) => ({
        ...prevForm,
        pagamentoOrigem: false,
        pagamentoDestino: true,
      }));
    }
  };

  // Manipular mudança nos volumes
  const handleVolumeChange = (index, e) => {
    const { name, value } = e.target;
    const newVolumes = [...form.volumes];
    newVolumes[index][name] = value;
    setForm({ ...form, volumes: newVolumes });
  };

  // Adicionar novo volume
  const addVolume = () => {
    setForm((prevForm) => ({
      ...prevForm,
      volumes: [
        ...prevForm.volumes,
        { altura: 10, largura: 10, comprimento: 10, peso: 0 },
      ],
    }));
  };

  // Remover volume
  const removeVolume = (index) => {
    const newVolumes = form.volumes.filter((_, i) => i !== index);
    setForm({ ...form, volumes: newVolumes });
  };

  // Resetar o formulário
  const limparFormulario = () => {
    setForm({
      cepOrigem: "74672856",
      cepDestino: "",
      valorNotaFiscal: "",
      entregaDomicilio: false,
      retiraTerminal: false,
      pagamentoOrigem: false,
      pagamentoDestino: false,
      volumes: [{ altura: 10, largura: 10, comprimento: 10, peso: 0 }],
    });
  };

  // Simulação de cotação
  const calcularCotacao = () => {
    console.log("Cotação enviada:", form);
    // Aqui será a integração com o backend
  };

  return (
    <div className="cotacao-container">
      <h1>Calculadora de Frete</h1>

      <div className="form-container">
        <div className="coluna-esquerda">
          <label>Origem:</label>
          <input type="text" value="GOIANIA-GO" disabled />

          <label>CEP de Destino:</label>
          <input
            type="text"
            name="cepDestino"
            value={form.cepDestino}
            onChange={handleChange}
          />

          <label>Valor total dos Produtos:</label>
          <input
            type="text"
            name="valorNotaFiscal"
            value={form.valorNotaFiscal}
            onChange={handleChange}
          />
        </div>

        <div className="coluna-direita">
          <div className="checkbox-group">
            <strong>Entrega Domicílio</strong>
            <label>
              <input
                type="checkbox"
                name="entregaDomicilio"
                checked={form.entregaDomicilio}
                onChange={() => handleEntregaChange("domicilio")}
              />
              Sim
            </label>
          </div>

          <div className="checkbox-group">
            <strong>Retira no Terminal de Cargas</strong>
            <label>
              <input
                type="checkbox"
                name="retiraTerminal"
                checked={form.retiraTerminal}
                onChange={() => handleEntregaChange("terminal")}
              />
              Sim
            </label>
          </div>
          <div className="checkbox-group">
            <strong>Pagamento</strong>
            <label>
              <input
                type="checkbox"
                name="pagamentoOrigem"
                checked={form.pagamentoOrigem}
                onChange={() => validarPagamento("origem")}
              />
              Origem
            </label>
            <label>
              <input
                type="checkbox"
                name="pagamentoDestino"
                checked={form.pagamentoDestino}
                onChange={() => validarPagamento("destino")}
              />
              Destinatário
            </label>
          </div>
        </div>
      </div>

      <div className="volumes-container">
        <h3>Dados do Volume</h3>
        {form.volumes.map((volume, index) => (
          <div key={index} className="volume-box">
            <label>Altura*</label>
            <input
              type="number"
              name="altura"
              value={volume.altura}
              onChange={(e) => handleVolumeChange(index, e)}
            />

            <label>Largura*</label>
            <input
              type="number"
              name="largura"
              value={volume.largura}
              onChange={(e) => handleVolumeChange(index, e)}
            />

            <label>Comprimento*</label>
            <input
              type="number"
              name="comprimento"
              value={volume.comprimento}
              onChange={(e) => handleVolumeChange(index, e)}
            />

            <label>Peso*</label>
            <input
              type="number"
              name="peso"
              value={volume.peso}
              onChange={(e) => handleVolumeChange(index, e)}
            />

            {index > 0 && (
              <button
                className="delete-button"
                onClick={() => removeVolume(index)}
              >
                🗑️
              </button>
            )}
          </div>
        ))}
        <button className="add-button" onClick={addVolume}>
          + Adicionar Volume
        </button>
      </div>

      <div className="button-group">
        <button className="limpar-button" onClick={limparFormulario}>
          LIMPAR
        </button>
        <button className="calcular-button" onClick={calcularCotacao}>
          CALCULAR
        </button>
      </div>

      <div className="resultado-container">
        <h2>Resultado da Cotação</h2>
        {form.retiraTerminal && form.cepDestino ? (
          <p>
            O cliente deverá retirar a mercadoria no aeroporto correspondente ao
            destino informado.
          </p>
        ) : (
          <p>Aqui será exibido o resultado do cálculo.</p>
        )}
      </div>
    </div>
  );
};

export default Cotacao;
